import { 
    Grid, 
    TextField, 
    withStyles, 
    InputLabel, 
    Select, 
    MenuItem, 
    FormControl 
} from "@material-ui/core";
import React from "react";
import useForm from "./useForm";

const styles = theme => ({
    root: {
        padding: theme.spacing(2),
        '& .MuiFormControl-root': {
            margin: theme.spacing(1, 0), // vertical margin only
            width: '100%',
        },
    },
    formControl: {
        minWidth: 230,
    }
});

const initialFieldValues = {
    fullName: '',
    mobile: '',
    email: '',
    age: '',
    bloodGroup: '',
    address: ''
};

const DCandidateForm = (props) => {
    const { values, setValues, handleInputChange } = useForm(initialFieldValues);
    const { classes } = props;

    return (
        <form autoComplete="off" noValidate className={classes.root}>
            <Grid container spacing={2}>
                <Grid item xs={6}>
                    <TextField
                        name="fullName"
                        variant="outlined"
                        label="Full Name"
                        value={values.fullName}
                        onChange={handleInputChange}
                        className={classes.formControl}
                    />
                    <TextField
                        name="email"
                        variant="outlined"
                        label="Email"
                        value={values.email}
                        onChange={handleInputChange}
                        className={classes.formControl}
                    />
                    <FormControl 
                        variant="outlined" 
                        className={classes.formControl}
                    >
                        <InputLabel>Blood Group</InputLabel>
                        <Select
                            name="bloodGroup"
                            value={values.bloodGroup}
                            onChange={handleInputChange}
                            label="Blood Group"
                        >
                            <MenuItem value="">Select Blood Group</MenuItem>
                            <MenuItem value="AB+">AB +ve</MenuItem>
                            <MenuItem value="AB-">AB -ve</MenuItem>
                            <MenuItem value="A+">A +ve</MenuItem>
                            <MenuItem value="A-">A -ve</MenuItem>
                            <MenuItem value="B+">B +ve</MenuItem>
                            <MenuItem value="B-">B -ve</MenuItem>
                            <MenuItem value="O+">O +ve</MenuItem>
                            <MenuItem value="O-">O -ve</MenuItem>
                        </Select>
                    </FormControl>
                </Grid>
                <Grid item xs={6}>
                    <TextField
                        name="mobile"
                        variant="outlined"
                        label="Mobile"
                        value={values.mobile}
                        onChange={handleInputChange}
                        className={classes.formControl}
                    />
                    <TextField
                        name="age"
                        variant="outlined"
                        label="Age"
                        value={values.age}
                        onChange={handleInputChange}
                        className={classes.formControl}
                    />
                    <TextField
                        name="address"
                        variant="outlined"
                        label="Address"
                        value={values.address}
                        onChange={handleInputChange}
                        className={classes.formControl}
                    />
                </Grid>
            </Grid>
        </form>
    );
};

export default withStyles(styles)(DCandidateForm);
